svg.script:
const unblock = document.getElementById('unblock');
const antorcha = document.getElementById('anforcha');

const unblockEnergy = unblock.querySelectorAll('.energy');
const unblockGas = unblock.querySelectorAll('.gas');

const antorchaGas = antorcha.querySelectorAll('.gas');
const antorchaGlow = antorcha.querySelectorAll('.fire-glow');
const antorchaFire = antorcha.querySelectorAll('.fire');

unblock.style.transition = "opacity 0.5s ease";
antorcha.style.transition = "opacity 0.5s ease";

unblock.style.opacity = "0.5";
antorcha.style.opacity = "1";

unblockEnergy.forEach(el => el.style.display = "none");
unblockGas.forEach(el => el.style.display = "none");

antorchaGas.forEach(el => el.style.display = "inline");
antorchaGlow.forEach(el => el.style.display = "inline");
antorchaFire.forEach(el => el.style.display = "inline");

let toggle = false;

function toggleOpacity() {
  if (toggle) {
    // Show unblock for 6s
    unblock.style.opacity = "1";
    antorcha.style.opacity = "0.5";

    unblockEnergy.forEach(el => el.style.display = "inline");
    unblockGas.forEach(el => el.style.display = "inline");

    antorchaGas.forEach(el => el.style.display = "none");
    antorchaGlow.forEach(el => el.style.display = "none");
    antorchaFire.forEach(el => el.style.display = "none");

    setTimeout(toggleOpacity, 3500);
  } else {
    // Show antorcha for 2s
    unblock.style.opacity = "0.5";
    antorcha.style.opacity = "1";

    unblockEnergy.forEach(el => el.style.display = "none");
    unblockGas.forEach(el => el.style.display = "none");

    antorchaGas.forEach(el => el.style.display = "inline");
    antorchaGlow.forEach(el => el.style.display = "inline");
    antorchaFire.forEach(el => el.style.display = "inline");

    setTimeout(toggleOpacity, 1700);
  }

  toggle = !toggle;
}

// Start the toggle loop
setTimeout(toggleOpacity, 1700); // initial duration matches antorcha
